try {
    module.exports = require('./32/sqlserver.node');   //For Azure     
}
catch (e) {    
    try {
	    module.exports = require('./64/sqlserver.node'); //For local
    }
    catch (e) {
		try{
			module.exports = require('./win8_64/sqlserver.node'); //For win8 local
		}
		catch (e) {
			console.error('No matched sqlserver.node found. Please try node-gyp configure & node-gyp build in current machine again.');
			throw e;
		}
    }
}